const suitabilityMap: Record<string, string[]> = {
    'hairfall': ['Diffuse Thinning', 'Pattern Hair Loss'],
    'hair fall': ['Diffuse Thinning', 'Pattern Hair Loss'],
    'growth': ['Pattern Hair Loss', 'Hairline Recession', 'Patchy Hair Loss'],
    'dandruff': ['Scalp Conditions'],
    'soothing': ['Scalp Conditions', 'Redness & Sensitivity'],
    'strengthening': ['Hair Breakage', 'Diffuse Thinning'],
    'breakage': ['Hair Breakage'],
    'healthy hair': ['Healthy Hair & Scalp'],
    'volumizing': ['Diffuse Thinning'],
    'thinning': ['Diffuse Thinning'],
};

const ingredientMap: Record<string, string[]> = {
    'redensyl': ['Redensyl'],
    'anagain': ['Anagain'],
    'caffeine': ['Caffeine'],
    'procapil': ['Procapil'],
    'capixyl': ['Capixyl'],
    'biotin': ['Biotin'],
    'ketoconazole': ['Ketoconazole'],
    'zinc pyrithione': ['Zinc Pyrithione'],
    'salicylic acid': ['Salicylic Acid'],
    'aloe vera': ['Aloe Vera'],
    'tea tree oil': ['Tea Tree Oil'],
    'keratin': ['Keratin'],
    'argan oil': ['Argan Oil'],
    'collagen': ['Collagen'],
};

const parseCSVAndEnrich = (data: string): WebsiteProduct[] => {
    const lines = data.trim().split('\n').slice(1);
    const products: WebsiteProduct[] = [];

    lines.forEach((line) => {
        const values = line.split(/,(?=(?:(?:[^"]*"){2})*[^"]*$)/);
        
        if (values.length >= 7) {
            const name = values[1].replace(/"/g, '').trim();
            const lowerCaseName = name.toLowerCase();
            const variantId = values[4].trim();
            const price = values[5].trim();
            const originalPrice = values[6].trim();

            if (!variantId || variantId === '0' || price === 'Rs. 0.00') {
                return;
            }

            const suitableFor = new Set<string>();
            for (const keyword in suitabilityMap) {
                if (lowerCaseName.includes(keyword)) {
                    suitabilityMap[keyword].forEach(cat => suitableFor.add(cat));
                }
            }

            const keyIngredients = new Set<string>();
            for (const keyword in ingredientMap) {
                if (lowerCaseName.includes(keyword)) {
                    ingredientMap[keyword].forEach(ing => keyIngredients.add(ing));
                }
            }

            const suitableForArray = Array.from(suitableFor);
            const keyIngredientsArray = Array.from(keyIngredients);
            
            let description = `A specialized formula, ${name}, designed to address various hair & scalp concerns.`;
            if (suitableForArray.length > 0) {
                const suitableForString = suitableForArray.map(s => s.toLowerCase().replace(/ & /g, ' and ')).join(', ');
                description = `A specialized formula, ${name}, designed to target ${suitableForString}.`;
                if (keyIngredientsArray.length > 0) {
                    description += ` Key ingredients include ${keyIngredientsArray.join(', ')}.`;
                }
            } else if (keyIngredientsArray.length > 0) {
                 description = `A specialized formula, ${name}, featuring key ingredients like ${keyIngredientsArray.join(', ')}.`;
            }

            const product: WebsiteProduct = {
                id: variantId,
                name: name,
                url: values[2].trim(),
                imageUrl: values[3].trim(),
                variantId: variantId,
                price: price,
                originalPrice: originalPrice,
                description: description,
                suitableFor: suitableForArray.length > 0 ? suitableForArray : ['Healthy Hair & Scalp'],
                keyIngredients: keyIngredientsArray,
            };
            
            products.push(product);
        }
    });

    return products;
};

export const DERMATICS_INDIA_PRODUCTS: WebsiteProduct[] = parseCSVAndEnrich(csvData);
